import React, { useEffect } from "react";

const PizzaBurgerCanvas = () => {
  useEffect(() => {
    // Draw Pizza
    const pizzaCanvas = document.getElementById("pizzaCanvas");
    const pizzaCtx = pizzaCanvas.getContext("2d");

    // Pizza Base
    pizzaCtx.fillStyle = "#ffcc99";
    pizzaCtx.beginPath();
    pizzaCtx.moveTo(75, 20);
    pizzaCtx.lineTo(125, 180);
    pizzaCtx.lineTo(25, 180);
    pizzaCtx.closePath();
    pizzaCtx.fill();

    // Pizza Crust
    pizzaCtx.fillStyle = "#d28c5a";
    pizzaCtx.beginPath();
    pizzaCtx.arc(75, 20, 30, Math.PI, 0);
    pizzaCtx.fill();

    // Pizza Toppings
    pizzaCtx.fillStyle = "#ff4d4d"; // Red circles (pepperoni)
    pizzaCtx.beginPath();
    pizzaCtx.arc(50, 90, 10, 0, 2 * Math.PI);
    pizzaCtx.arc(100, 120, 10, 0, 2 * Math.PI);
    pizzaCtx.arc(65, 140, 10, 0, 2 * Math.PI);
    pizzaCtx.fill();

    pizzaCtx.fillStyle = "#00cc66"; // Green circles (veggies)
    pizzaCtx.beginPath();
    pizzaCtx.arc(85, 70, 5, 0, 2 * Math.PI);
    pizzaCtx.arc(55, 130, 5, 0, 2 * Math.PI);
    pizzaCtx.arc(75, 150, 5, 0, 2 * Math.PI);
    pizzaCtx.fill();

    // Draw Burger
    const burgerCanvas = document.getElementById("burgerCanvas");
    const burgerCtx = burgerCanvas.getContext("2d");

    // Bottom Bun
    burgerCtx.fillStyle = "#ffcc99";
    burgerCtx.beginPath();
    burgerCtx.arc(75, 140, 50, 0, Math.PI, true);
    burgerCtx.fill();

    // Patty
    burgerCtx.fillStyle = "#804000";
    burgerCtx.fillRect(25, 90, 100, 20);

    // Lettuce
    burgerCtx.fillStyle = "#00cc66";
    burgerCtx.beginPath();
    burgerCtx.arc(75, 90, 50, Math.PI, 0, true);
    burgerCtx.fill();

    // Top Bun
    burgerCtx.fillStyle = "#ffcc99";
    burgerCtx.beginPath();
    burgerCtx.arc(75, 60, 50, 0, Math.PI * 2);
    burgerCtx.fill();

    // Sesame Seeds
    burgerCtx.fillStyle = "#fff";
    burgerCtx.beginPath();
    burgerCtx.arc(55, 45, 5, 0, Math.PI * 2);
    burgerCtx.arc(75, 35, 5, 0, Math.PI * 2);
    burgerCtx.arc(95, 45, 5, 0, Math.PI * 2);
    burgerCtx.fill();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Custom Pizza & Burger</h2>
      <div style={styles.canvasContainer}>
        <canvas id="pizzaCanvas" width="150" height="200" style={styles.canvas}></canvas>
        <canvas id="burgerCanvas" width="150" height="200" style={styles.canvas}></canvas>
      </div>
    </div>
  );
};

const styles = {
  container: {
    textAlign: "center",
    padding: "20px",
    background: "linear-gradient(135deg, #f7d9e3, #fcc2e4)",
    borderRadius: "10px",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
    maxWidth: "400px",
    margin: "auto",
  },
  heading: {
    color: "#ff77aa",
    fontFamily: "'Poppins', sans-serif",
    marginBottom: "20px",
  },
  canvasContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "20px",
  },
  canvas: {
    border: "2px solid #ff69b4",
    borderRadius: "10px",
    backgroundColor: "#fffaf0",
  },
};

export default PizzaBurgerCanvas;
