import React from "react";
import { useParams } from "react-router-dom";

const ProductDetail = () => {
  const { id } = useParams(); // Get product ID from URL

  // Define product data dynamically (description, features, etc.)
  const productDetails = {
    1: {
      name: "Product 1",
      price: "100 INR",
      description: "This is a detailed description of Product 1. It has amazing features and great value.",
      features: ["Feature 1", "Feature 2", "Feature 3"],
      images: ["https://via.placeholder.com/300?text=Image+1", "https://via.placeholder.com/300?text=Image+2"],
    },
    2: {
      name: "Product 2",
      price: "200 INR",
      description: "This is Product 2. It comes with enhanced durability and sleek design.",
      features: ["Feature 1", "Feature 2", "Feature 3"],
      images: ["https://via.placeholder.com/300?text=Image+1", "https://via.placeholder.com/300?text=Image+2"],
    },
    // Add similar data for all 24 products...
  };

  const product = productDetails[id];

  const pageStyle = {
    padding: "2rem",
    fontFamily: "Arial, sans-serif",
    background: "linear-gradient(135deg, #f7f9fc, #d7e8f7)",
    color: "#333",
    lineHeight: "1.6",
  };

  const containerStyle = {
    display: "grid",
    gridTemplateColumns: "1fr 2fr",
    gap: "2rem",
  };

  const imageGalleryStyle = {
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
  };

  const imageStyle = {
    width: "100%",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  };

  const detailsStyle = {
    background: "#ffffff",
    padding: "2rem",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
  };

  const sectionStyle = {
    marginBottom: "1.5rem",
  };

  const buttonStyle = {
    padding: "0.8rem 2rem",
    background: "#3498db",
    color: "white",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
  };

  return (
    <div style={pageStyle}>
      <h1 style={{ textAlign: "center", marginBottom: "2rem", color: "#2c3e50" }}>
        {product.name}
      </h1>
      <div style={containerStyle}>
        <div style={imageGalleryStyle}>
          {product.images.map((src, index) => (
            <img key={index} src={src} alt={`Product ${product.name} - ${index + 1}`} style={imageStyle} />
          ))}
        </div>
        <div style={detailsStyle}>
          <div style={sectionStyle}>
            <h2 style={{ color: "#8e44ad" }}>Price: {product.price}</h2>
          </div>
          <div style={sectionStyle}>
            <h3 style={{ color: "#8e44ad" }}>Description:</h3>
            <p>{product.description}</p>
          </div>
          <div style={sectionStyle}>
            <h3 style={{ color: "#8e44ad" }}>Features:</h3>
            <ul>
              {product.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
          </div>
          <button style={buttonStyle}>Buy Now</button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
