import React from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  return (
    <nav style={styles.navbar}>
      {/* Logo */}
      <div style={styles.logoContainer}>
        <span style={styles.logo}>GlowBrand</span>
      </div>

      {/* Navigation Links */}
      <ul style={styles.navItems}>
        <li style={styles.navItem}>
          <Link to="/" style={styles.navLink}>
            Home
          </Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/products" style={styles.navLink}>
            Products
          </Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/about" style={styles.navLink}>
            About
          </Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/contact" style={styles.navLink}>
            Contact
          </Link>
        </li>
      </ul>

      {/* Action Buttons */}
      <div style={styles.actionButtons}>
        <Link to="/login" style={{ ...styles.button, backgroundColor: "#4dffc3" }}>
          Login
        </Link>
        <Link to="/signup" style={{ ...styles.button, backgroundColor: "#ff4dff" }}>
          Sign Up
        </Link>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "1rem 2rem",
    background: "linear-gradient(135deg, #1e1e2f, #282841)",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.5)",
    position: "fixed",
    width: "100%",
    zIndex: 1000,
  },
  logoContainer: {
    fontWeight: "bold",
    fontSize: "1.8rem",
    color: "#4dffc3",
    textTransform: "uppercase",
    cursor: "pointer",
    animation: "glow 2s infinite",
  },
  navItems: {
    display: "flex",
    listStyle: "none",
    margin: 0,
    padding: 0,
  },
  navItem: {
    margin: "0 1rem",
    position: "relative",
  },
  navLink: {
    textDecoration: "none",
    color: "#fff",
    fontSize: "1rem",
    fontWeight: "500",
    position: "relative",
    transition: "color 0.3s ease",
  },
  navLinkHover: {
    color: "#4dffc3",
  },
  actionButtons: {
    display: "flex",
    gap: "1rem",
  },
  button: {
    textDecoration: "none",
    padding: "0.6rem 1.2rem",
    borderRadius: "25px",
    fontSize: "0.9rem",
    color: "#fff",
    fontWeight: "bold",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
    transition: "transform 0.3s ease, background 0.3s ease",
    cursor: "pointer",
  },
};

export default Navbar;
