import React from "react";

const products = [
  { id: 1, name: "Product 1", price: "$29.99", img: "image1.jpg" },
  { id: 2, name: "Product 2", price: "$39.99", img: "image2.jpg" },
  { id: 3, name: "Product 3", price: "$49.99", img: "image3.jpg" },
  { id: 4, name: "Product 4", price: "$19.99", img: "image4.jpg" },
  { id: 5, name: "Product 5", price: "$24.99", img: "image5.jpg" },
  // Add remaining products similarly
  { id: 24, name: "Product 24", price: "$59.99", img: "image24.jpg" },
];

const ProductPage = () => {
  const containerStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "1.5rem",
    padding: "2rem",
    background: "linear-gradient(135deg, #f3f4f6, #ffffff)",
  };

  const cardStyle = {
    backgroundColor: "#ffffff",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    overflow: "hidden",
    textAlign: "center",
    transition: "transform 0.3s ease",
  };

  const cardHover = {
    ":hover": {
      transform: "translateY(-5px)",
      boxShadow: "0 6px 12px rgba(0, 0, 0, 0.2)",
    },
  };

  const imgStyle = {
    width: "100%",
    height: "200px",
    objectFit: "cover",
  };

  const nameStyle = {
    fontSize: "1.2rem",
    color: "#333",
    margin: "0.8rem 0",
  };

  const priceStyle = {
    fontSize: "1rem",
    color: "#3498db",
    fontWeight: "bold",
  };

  const buttonStyle = {
    padding: "0.5rem 1rem",
    margin: "1rem 0",
    border: "none",
    borderRadius: "20px",
    background: "#8e44ad",
    color: "#fff",
    cursor: "pointer",
    transition: "transform 0.3s ease, background-color 0.3s ease",
  };

  const buttonHover = {
    ":hover": {
      transform: "scale(1.05)",
      backgroundColor: "#9b59b6",
    },
  };

  return (
    <div style={containerStyle}>
      {products.map((product) => (
        <div key={product.id} style={{ ...cardStyle, ...cardHover }}>
          <img src={product.img} alt={product.name} style={imgStyle} />
          <h3 style={nameStyle}>{product.name}</h3>
          <p style={priceStyle}>{product.price}</p>
          <button style={{ ...buttonStyle, ...buttonHover }}>Buy Now</button>
        </div>
      ))}
    </div>
  );
};

export default ProductPage;
