import React, { useState } from "react";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => setIsLogin(!isLogin);

  const pageStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    background: "linear-gradient(120deg, #2980b9, #2c3e50)",
    color: "#fff",
  };

  const containerStyle = {
    width: "400px",
    height: "500px",
    background: "#2d3436",
    borderRadius: "10px",
    overflow: "hidden",
    boxShadow: "0 8px 15px rgba(0, 0, 0, 0.2)",
    position: "relative",
  };

  const sliderStyle = {
    display: "flex",
    transform: `translateX(${isLogin ? "0" : "-100%"})`,
    transition: "transform 0.5s ease-in-out",
    width: "200%",
  };

  const formStyle = {
    width: "50%",
    padding: "2rem",
    boxSizing: "border-box",
    textAlign: "center",
  };

  const inputStyle = {
    width: "100%",
    padding: "0.8rem",
    margin: "0.8rem 0",
    borderRadius: "25px",
    border: "1px solid #ccc",
    fontSize: "1rem",
    outline: "none",
    transition: "border-color 0.3s ease",
  };

  const buttonStyle = {
    width: "100%",
    padding: "1rem",
    background: "#3498db",
    color: "#fff",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    fontSize: "1.1rem",
    transition: "background 0.3s ease",
  };

  const toggleButtonStyle = {
    marginTop: "1rem",
    fontSize: "0.9rem",
    color: "#fff",
    cursor: "pointer",
    textDecoration: "underline",
  };

  return (
    <div style={pageStyle}>
      <div style={containerStyle}>
        <div style={{ overflow: "hidden", width: "100%" }}>
          <div style={sliderStyle}>
            {/* Login Form */}
            <div style={formStyle}>
              <h2>Login</h2>
              <input
                type="email"
                placeholder="Email"
                style={inputStyle}
              />
              <input
                type="password"
                placeholder="Password"
                style={inputStyle}
              />
              <button style={buttonStyle}>Login</button>
              <div style={toggleButtonStyle} onClick={toggleForm}>
                Don't have an account? Sign Up
              </div>
            </div>

            {/* Signup Form */}
            <div style={formStyle}>
              <h2>Sign Up</h2>
              <input
                type="text"
                placeholder="Full Name"
                style={inputStyle}
              />
              <input
                type="email"
                placeholder="Email"
                style={inputStyle}
              />
              <input
                type="password"
                placeholder="Password"
                style={inputStyle}
              />
              <button style={buttonStyle}>Sign Up</button>
              <div style={toggleButtonStyle} onClick={toggleForm}>
                Already have an account? Login
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
