import React from 'react';
import { Link } from 'react-router-dom';

// Sample product data
const products = Array.from({ length: 25 }, (_, index) => ({
  id: index + 1,
  name: `Product ${index + 1}`,
  price: (Math.random() * 100 + 10).toFixed(2),
  image: `https://via.placeholder.com/150?text=Product+${index + 1}`,
}));

const Product = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Our Products</h1>
      <div style={styles.grid}>
        {products.map((product) => (
          <div > <Link to={`/product/${product.id}`} key={product.id} style={styles.card}>
            <img src={product.image} alt={product.name} style={styles.image} />
            <h2 style={styles.productName}>{product.name}</h2>
            <p style={styles.price}>${product.price}</p>
            <button style={styles.button}>Buy Now</button></Link>

          </div>

        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px 40px',
    backgroundColor: '#f4f8fb',
    textAlign: 'center',
  },
  heading: {
    fontSize: '32px',
    marginBottom: '20px',
    color: '#333',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
    gap: '20px',
  },
  card: {
    backgroundColor: 'white',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    padding: '15px',
    textAlign: 'center',
  },
  image: {
    width: '100%',
    height: '150px',
    objectFit: 'cover',
    borderRadius: '8px',
  },
  productName: {
    fontSize: '18px',
    margin: '10px 0',
    color: '#4a90e2',
  },
  price: {
    fontSize: '16px',
    color: '#50c9c3',
    marginBottom: '10px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    color: 'white',
    backgroundColor: '#4a90e2',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
};

export default Product;
