import React, { useState } from "react";

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => setIsLogin(!isLogin);

  const containerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    background: "linear-gradient(135deg, #f3e7e9, #e3eeff)",
    fontFamily: "'Roboto', sans-serif",
  };

  const formWrapperStyle = {
    width: "400px",
    background: "#fff",
    borderRadius: "15px",
    boxShadow: "0 8px 20px rgba(0, 0, 0, 0.1)",
    overflow: "hidden",
    position: "relative",
  };

  const sliderStyle = {
    height: "4px",
    background: isLogin ? "#6a11cb" : "#2575fc",
    position: "absolute",
    top: 0,
    left: isLogin ? "0" : "50%",
    width: "50%",
    transition: "left 0.4s ease",
  };

  const formStyle = {
    padding: "2rem",
    textAlign: "center",
  };

  const inputStyle = {
    width: "100%",
    padding: "0.8rem",
    margin: "1rem 0",
    border: "1px solid #ddd",
    borderRadius: "8px",
    fontSize: "1rem",
  };

  const buttonStyle = {
    padding: "0.8rem 2rem",
    background: isLogin ? "#6a11cb" : "#2575fc",
    color: "#fff",
    border: "none",
    borderRadius: "25px",
    fontSize: "1rem",
    cursor: "pointer",
    transition: "background 0.3s ease",
  };

  const toggleButtonStyle = {
    marginTop: "1rem",
    color: isLogin ? "#2575fc" : "#6a11cb",
    cursor: "pointer",
    textDecoration: "underline",
  };

  return (
    <div style={containerStyle}>
      <div style={formWrapperStyle}>
        <div style={sliderStyle}></div>
        {isLogin ? (
          <div style={formStyle}>
            <h2 style={{ marginBottom: "1.5rem", color: "#333" }}>Login</h2>
            <input type="email" placeholder="Email" style={inputStyle} />
            <input type="password" placeholder="Password" style={inputStyle} />
            <button style={buttonStyle}>Login</button>
            <p style={toggleButtonStyle} onClick={toggleForm}>
              Don't have an account? Sign up
            </p>
          </div>
        ) : (
          <div style={formStyle}>
            <h2 style={{ marginBottom: "1.5rem", color: "#333" }}>Sign Up</h2>
            <input type="text" placeholder="Full Name" style={inputStyle} />
            <input type="email" placeholder="Email" style={inputStyle} />
            <input type="password" placeholder="Password" style={inputStyle} />
            <input type="password" placeholder="Confirm Password" style={inputStyle} />
            <button style={buttonStyle}>Sign Up</button>
            <p style={toggleButtonStyle} onClick={toggleForm}>
              Already have an account? Login
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AuthForm;
