import React, { useState } from "react";

const AuthForm = () => {
  const [isSignup, setIsSignup] = useState(true);

  const toggleForm = () => {
    setIsSignup(!isSignup);
  };

  const containerStyle = {
    fontFamily: "Arial, sans-serif",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    background: "linear-gradient(135deg, #f0f4f8, #d9e4f1)",
  };

  const formWrapperStyle = {
    background: "white",
    width: "100%",
    maxWidth: "400px",
    borderRadius: "10px",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)",
    padding: "2rem",
    textAlign: "center",
  };

  const toggleButtonStyle = {
    background: "transparent",
    border: "none",
    color: "#3498db",
    fontSize: "1rem",
    fontWeight: "bold",
    cursor: "pointer",
    textDecoration: "underline",
    marginTop: "1rem",
  };

  const headingStyle = {
    color: "#2c3e50",
    fontSize: "1.5rem",
    marginBottom: "1.5rem",
  };

  const inputStyle = {
    width: "100%",
    padding: "0.8rem",
    margin: "0.5rem 0",
    borderRadius: "5px",
    border: "1px solid #ccc",
    fontSize: "1rem",
  };

  const buttonStyle = {
    width: "100%",
    padding: "0.8rem",
    margin: "1rem 0",
    borderRadius: "25px",
    background: "#3498db",
    color: "white",
    border: "none",
    cursor: "pointer",
    fontSize: "1rem",
    transition: "background 0.3s ease",
  };

  const formTitle = isSignup ? "Create Account" : "Welcome Back";

  return (
    <div style={containerStyle}>
      <div style={formWrapperStyle}>
        <h2 style={headingStyle}>{formTitle}</h2>
        <form>
          {isSignup && (
            <input
              type="text"
              placeholder="Full Name"
              style={inputStyle}
              required
            />
          )}
          <input
            type="email"
            placeholder="Email Address"
            style={inputStyle}
            required
          />
          <input
            type="password"
            placeholder="Password"
            style={inputStyle}
            required
          />
          {isSignup && (
            <input
              type="password"
              placeholder="Confirm Password"
              style={inputStyle}
              required
            />
          )}
          <button type="submit" style={buttonStyle}>
            {isSignup ? "Sign Up" : "Log In"}
          </button>
        </form>
        <button style={toggleButtonStyle} onClick={toggleForm}>
          {isSignup
            ? "Already have an account? Log In"
            : "Don't have an account? Sign Up"}
        </button>
      </div>
    </div>
  );
};

export default AuthForm;
