import React, { useState } from "react";

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => setIsLogin(!isLogin);

  const containerStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
    background: "linear-gradient(135deg, #f7f9fc, #c3d8f0)",
    fontFamily: "'Arial', sans-serif",
  };

  const cardStyle = {
    width: "400px",
    padding: "2rem",
    borderRadius: "15px",
    background: "#ffffff",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
    overflow: "hidden",
    position: "relative",
  };

  const sliderStyle = {
    position: "absolute",
    top: "0",
    left: isLogin ? "0" : "-100%",
    width: "200%",
    height: "100%",
    display: "flex",
    transition: "left 0.6s ease",
  };

  const formStyle = {
    flex: "1",
    padding: "2rem",
    textAlign: "center",
  };

  const inputStyle = {
    width: "100%",
    marginBottom: "1rem",
    padding: "0.8rem",
    borderRadius: "10px",
    border: "1px solid #ddd",
    outline: "none",
    fontSize: "1rem",
  };

  const buttonStyle = {
    padding: "0.8rem 1.5rem",
    background: "#3498db",
    color: "#fff",
    border: "none",
    borderRadius: "20px",
    fontSize: "1rem",
    cursor: "pointer",
    marginTop: "1rem",
    transition: "background 0.3s ease",
  };

  const toggleStyle = {
    marginTop: "1rem",
    color: "#3498db",
    cursor: "pointer",
    textDecoration: "underline",
    fontSize: "0.9rem",
  };

  return (
    <div style={containerStyle}>
      <div style={cardStyle}>
        <div style={sliderStyle}>
          {/* Login Form */}
          <div style={formStyle}>
            <h2>Login</h2>
            <input type="email" placeholder="Email" style={inputStyle} />
            <input type="password" placeholder="Password" style={inputStyle} />
            <button style={buttonStyle}>Login</button>
            <p style={toggleStyle} onClick={toggleForm}>
              Don't have an account? Sign up
            </p>
          </div>

          {/* Signup Form */}
          <div style={formStyle}>
            <h2>Sign Up</h2>
            <input type="text" placeholder="Name" style={inputStyle} />
            <input type="email" placeholder="Email" style={inputStyle} />
            <input type="password" placeholder="Password" style={inputStyle} />
            <input type="password" placeholder="Confirm Password" style={inputStyle} />
            <button style={buttonStyle}>Sign Up</button>
            <p style={toggleStyle} onClick={toggleForm}>
              Already have an account? Login
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
