import React, { useState } from "react";
import { useParams } from "react-router-dom";

const OrderForm = () => {
  const { id } = useParams(); // Get product ID from URL
  const [step, setStep] = useState(1);
  const [orderData, setOrderData] = useState({
    quantity: 1,
    address: "",
    contact: "",
    paymentMethod: "Online",
  });

  // Mock product data
  const products = Array.from({ length: 24 }, (_, index) => ({
    id: (index + 1).toString(),
    name: `Product ${index + 1}`,
    price: `${(index + 1) * 100}`,
    image: index % 2 === 0 ? `https://via.placeholder.com/150?text=Product+${index + 1}` : null,
    description: `This is a brief description of Product ${index + 1}.`,
  }));

  // Find the selected product
  const product = products.find((p) => p.id === id);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderData((prev) => ({ ...prev, [name]: value }));
  };

  const handleNext = () => setStep((prev) => prev + 1);
  const handlePrevious = () => setStep((prev) => prev - 1);

  const calculateTotalPrice = () => {
    return parseInt(product.price) * parseInt(orderData.quantity);
  };

  const pageStyle = {
    padding: "2rem",
    fontFamily: "Arial, sans-serif",
    background: "linear-gradient(135deg, #f7f9fc, #d7e8f7)",
    color: "#333",
    borderRadius: "10px",
    maxWidth: "600px",
    margin: "auto",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  };

  const buttonStyle = {
    margin: "1rem 0.5rem",
    padding: "0.8rem 2rem",
    background: "#3498db",
    color: "white",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    fontSize: "1rem",
    transition: "background 0.3s ease",
  };

  if (!product) return <h2 style={{ textAlign: "center" }}>Product Not Found!</h2>;

  return (
    <div style={pageStyle}>
      <h1 style={{ textAlign: "center", marginBottom: "2rem", color: "#2c3e50" }}>
        Order for {product.name}
      </h1>
      {step === 1 && (
        <>
          <h3>Select Quantity</h3>
          <input
            type="number"
            name="quantity"
            value={orderData.quantity}
            min="1"
            onChange={handleInputChange}
            style={{ width: "100%", padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
          />
          <div>
            <button style={buttonStyle} onClick={handleNext}>
              Next
            </button>
          </div>
        </>
      )}
      {step === 2 && (
        <>
          <h3>Delivery Address</h3>
          <textarea
            name="address"
            value={orderData.address}
            onChange={handleInputChange}
            rows="4"
            style={{ width: "100%", padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
          />
          <h3>Contact Information</h3>
          <input
            type="text"
            name="contact"
            value={orderData.contact}
            onChange={handleInputChange}
            style={{ width: "100%", padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
          />
          <div>
            <button style={buttonStyle} onClick={handlePrevious}>
              Previous
            </button>
            <button style={buttonStyle} onClick={handleNext}>
              Next
            </button>
          </div>
        </>
      )}
      {step === 3 && (
        <>
          <h3>Select Payment Method</h3>
          <select
            name="paymentMethod"
            value={orderData.paymentMethod}
            onChange={handleInputChange}
            style={{ width: "100%", padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
          >
            <option value="Online">Online Payment</option>
            <option value="COD">Cash on Delivery</option>
          </select>
          <div>
            <button style={buttonStyle} onClick={handlePrevious}>
              Previous
            </button>
            <button style={buttonStyle} onClick={handleNext}>
              Next
            </button>
          </div>
        </>
      )}
      {step === 4 && (
        <>
          <h3>Order Summary</h3>
          <div style={{ textAlign: "center", marginBottom: "1rem" }}>
            <img
              src={product.image || "https://via.placeholder.com/150?text=No+Image"}
              alt={product.name}
              style={{ width: "100%", maxHeight: "200px", borderRadius: "10px" }}
            />
          </div>
          <p><strong>Product ID:</strong> {product.id}</p>
          <p><strong>Product Name:</strong> {product.name}</p>
          <p><strong>Price per Unit:</strong> {product.price} INR</p>
          <p><strong>Quantity:</strong> {orderData.quantity}</p>
          <p><strong>Total Price:</strong> {calculateTotalPrice()} INR</p>
          <p><strong>Address:</strong> {orderData.address}</p>
          <p><strong>Contact:</strong> {orderData.contact}</p>
          <p><strong>Payment Method:</strong> {orderData.paymentMethod}</p>
          <div>
            <button style={buttonStyle} onClick={handlePrevious}>
              Previous
            </button>
            <button style={buttonStyle} onClick={() => alert("Order Confirmed!")}>
              Confirm Order
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default OrderForm;
