import React, { useState } from "react";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => setIsLogin(!isLogin);

  const containerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    background: "#f7f7f7",
    overflow: "hidden",
    transition: "all 0.3s ease-in-out",
  };

  const sliderStyle = {
    display: "flex",
    width: "200%",
    transition: "transform 0.5s ease",
    transform: `translateX(${isLogin ? "0" : "-50%"})`,
  };

  const formStyle = {
    width: "50%",
    padding: "2rem",
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background: "#fff",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
    borderRadius: "8px",
    overflow: "hidden",
  };

  const headingStyle = {
    fontSize: "2rem",
    color: "#333",
    marginBottom: "1.5rem",
  };

  const inputStyle = {
    width: "80%",
    padding: "1rem",
    margin: "1rem 0",
    borderRadius: "25px",
    border: "1px solid #ddd",
    outline: "none",
    fontSize: "1rem",
    transition: "border-color 0.3s ease",
  };

  const buttonStyle = {
    width: "80%",
    padding: "1rem",
    background: "#3498db",
    color: "#fff",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    fontSize: "1rem",
    transition: "background-color 0.3s ease",
  };

  const toggleButtonStyle = {
    marginTop: "1rem",
    fontSize: "1rem",
    color: "#3498db",
    cursor: "pointer",
    textDecoration: "underline",
  };

  return (
    <div style={containerStyle}>
      <div style={sliderStyle}>
        {/* Login Form */}
        <div style={formStyle}>
          <h2 style={headingStyle}>Login</h2>
          <input
            type="email"
            placeholder="Email"
            style={inputStyle}
          />
          <input
            type="password"
            placeholder="Password"
            style={inputStyle}
          />
          <button style={buttonStyle}>Login</button>
          <div style={toggleButtonStyle} onClick={toggleForm}>
            Don't have an account? Sign Up
          </div>
        </div>

        {/* Signup Form */}
        <div style={formStyle}>
          <h2 style={headingStyle}>Sign Up</h2>
          <input
            type="text"
            placeholder="Full Name"
            style={inputStyle}
          />
          <input
            type="email"
            placeholder="Email"
            style={inputStyle}
          />
          <input
            type="password"
            placeholder="Password"
            style={inputStyle}
          />
          <button style={buttonStyle}>Sign Up</button>
          <div style={toggleButtonStyle} onClick={toggleForm}>
            Already have an account? Login
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
