import React, { useState } from "react";

const OrderPage = () => {
  const [paymentMethod, setPaymentMethod] = useState("");

  const handleOrder = () => {
    if (!paymentMethod) {
      alert("Please select a payment method.");
      return;
    }
    alert(`Order placed successfully with ${paymentMethod}!`);
  };

  const pageStyle = {
    padding: "2rem",
    fontFamily: "Arial, sans-serif",
    background: "linear-gradient(135deg, #f7f9fc, #d7e8f7)",
    color: "#333",
  };

  const containerStyle = {
    maxWidth: "600px",
    margin: "0 auto",
    background: "#fff",
    padding: "2rem",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
  };

  const titleStyle = {
    textAlign: "center",
    color: "#2c3e50",
    marginBottom: "1.5rem",
  };

  const buttonStyle = {
    display: "block",
    width: "100%",
    padding: "1rem",
    background: "#3498db",
    color: "white",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    fontSize: "1.1rem",
    marginTop: "1rem",
    transition: "background 0.3s ease",
  };

  const handleButtonHover = {
    ":hover": {
      background: "#2980b9",
    },
  };

  return (
    <div style={pageStyle}>
      <div style={containerStyle}>
        <h1 style={titleStyle}>Complete Your Order</h1>
        <div>
          <h3>Choose Payment Method</h3>
          <label>
            <input
              type="radio"
              name="payment"
              value="Online Payment"
              onChange={(e) => setPaymentMethod(e.target.value)}
            />
            Online Payment
          </label>
          <br />
          <label>
            <input
              type="radio"
              name="payment"
              value="Cash on Delivery"
              onChange={(e) => setPaymentMethod(e.target.value)}
            />
            Cash on Delivery
          </label>
        </div>
        <button
          style={{ ...buttonStyle, ...handleButtonHover }}
          onClick={handleOrder}
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default OrderPage;
