import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import products from "./products";

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  // Find the product based on the ID
  const product = products.find((item) => item.id === parseInt(id));

  const handleBuyNow = () => {
    // Navigate to BuyNow page with product details passed via state
    navigate("/buy-now", { state: { product } });
  };

  return (
    <div style={{ padding: "2rem", backgroundColor: "#f8f9fa" }}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          backgroundColor: "#fff",
          borderRadius: "10px",
          padding: "2rem",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        {/* Product Image Carousel */}
        <div
          style={{
            display: "flex",
            overflowX: "scroll",
            gap: "10px",
            marginBottom: "1rem",
            maxWidth: "400px",
            borderRadius: "10px",
          }}
        >
          {product.images.map((img, index) => (
            <img
              key={index}
              src={img}
              alt={`Product ${index + 1}`}
              style={{
                width: "100%",
                height: "auto",
                maxWidth: "100px",
                borderRadius: "10px",
                objectFit: "cover",
              }}
            />
          ))}
        </div>

        {/* Product Details */}
        <h1 style={{ color: "#2c3e50" }}>{product.name}</h1>
        <p style={{ fontSize: "1.25rem", color: "#8e44ad" }}>
          Price: {product.price}
        </p>
        <p style={{ marginBottom: "1rem", textAlign: "center" }}>
          {product.description}
        </p>

        {/* Buy Now Button */}
        <button
          onClick={handleBuyNow}
          style={{
            backgroundColor: "#3498db",
            color: "#fff",
            padding: "0.75rem 1.5rem",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Buy Now
        </button>
      </div>
    </div>
  );
};

export default ProductDetail;
