import React from "react";
import { useNavigate } from "react-router-dom";
import products from "./products";

const ProductPage = () => {
  const navigate = useNavigate();

  return (
    <div style={{ padding: "2rem" }}>
      <h1 style={{ textAlign: "center", marginBottom: "2rem", color: "#3498db" }}>Our Products</h1>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "1.5rem",
        }}
      >
        {products.map((product) => (
          <div
            key={product.id}
            style={{
              background: "#fff",
              padding: "1rem",
              borderRadius: "10px",
              boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
              textAlign: "center",
            }}
          >
            <img
              src={product.image || "https://via.placeholder.com/150"}
              alt={product.name}
              style={{
                width: "100%",
                height: "150px",
                objectFit: "cover",
                marginBottom: "1rem",
              }}
            />
            <h3>{product.name}</h3>
            <p style={{ color: "#8e44ad", fontWeight: "bold" }}>{product.price}</p>
            <button
              style={{
                padding: "0.5rem 1rem",
                background: "#3498db",
                color: "#fff",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
              onClick={() => navigate(`/product/${product.id}`)}
            >
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
