import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Fetch product details from API or local data based on the product ID
    const fetchProductDetails = () => {
      const fetchedProduct = {
        id,
        name: `Product ${id}`,
        price: `${id * 100} INR`,
        image: `https://via.placeholder.com/300?text=Product+${id}`,
        description: `This is a detailed description of Product ${id}.`,
      };
      setProduct(fetchedProduct);
    };

    fetchProductDetails();
  }, [id]);

  if (!product) return <div>Loading...</div>;

  return (
    <div style={{ padding: "2rem" }}>
      <h1>{product.name}</h1>
      <img src={product.image} alt={product.name} style={{ width: "300px" }} />
      <p>{product.description}</p>
      <p style={{ fontWeight: "bold", color: "#3498db" }}>{product.price}</p>
      <Link to={`/order/${product.id}`} style={{ padding: "0.8rem 1.5rem", backgroundColor: "#3498db", color: "white", borderRadius: "25px", textDecoration: "none" }}>
        Buy Now
      </Link>
    </div>
  );
};

export default ProductDetail;
