import React, { useState } from "react";

const PlaceOrder = () => {
  const [step, setStep] = useState(1);
  const [orderDetails, setOrderDetails] = useState({
    quantity: 1,
    address: "",
    contact: "",
    paymentMethod: "COD",
  });

  const handleNext = () => {
    if (step < 4) setStep(step + 1);
  };

  const handleBack = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleChange = (field, value) => {
    setOrderDetails((prev) => ({ ...prev, [field]: value }));
  };

  const handlePlaceOrder = () => {
    alert("Order placed successfully!");
    console.log(orderDetails);
  };

  const stepStyle = {
    display: "flex",
    justifyContent: "space-between",
    padding: "1rem 0",
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif", background: "#f7f9fc" }}>
      <h1 style={{ textAlign: "center", color: "#2c3e50" }}>Place Your Order</h1>
      <div style={stepStyle}>
        <button onClick={handleBack} disabled={step === 1}>
          Back
        </button>
        <div>Step {step} of 4</div>
        <button onClick={handleNext} disabled={step === 4}>
          Next
        </button>
      </div>
      <div style={{ marginTop: "2rem", background: "#fff", padding: "1rem", borderRadius: "10px" }}>
        {step === 1 && (
          <div>
            <h3>Quantity</h3>
            <input
              type="number"
              value={orderDetails.quantity}
              min="1"
              onChange={(e) => handleChange("quantity", e.target.value)}
              style={{ padding: "0.5rem", width: "100px" }}
            />
          </div>
        )}
        {step === 2 && (
          <div>
            <h3>Address</h3>
            <textarea
              value={orderDetails.address}
              onChange={(e) => handleChange("address", e.target.value)}
              rows="4"
              style={{ width: "100%", padding: "0.5rem" }}
              placeholder="Enter your full address"
            />
          </div>
        )}
        {step === 3 && (
          <div>
            <h3>Contact Information</h3>
            <input
              type="text"
              value={orderDetails.contact}
              onChange={(e) => handleChange("contact", e.target.value)}
              style={{ padding: "0.5rem", width: "100%" }}
              placeholder="Enter your phone number"
            />
          </div>
        )}
        {step === 4 && (
          <div>
            <h3>Payment Method</h3>
            <select
              value={orderDetails.paymentMethod}
              onChange={(e) => handleChange("paymentMethod", e.target.value)}
              style={{ padding: "0.5rem", width: "100%" }}
            >
              <option value="COD">Cash on Delivery</option>
              <option value="Online">Online Payment</option>
            </select>
            <button
              onClick={handlePlaceOrder}
              style={{
                marginTop: "1rem",
                padding: "0.8rem 2rem",
                background: "#3498db",
                color: "white",
                border: "none",
                borderRadius: "25px",
                cursor: "pointer",
              }}
            >
              Place Order
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default PlaceOrder;
