import React, { useState } from "react";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => setIsLogin(!isLogin);

  const pageStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    background: "linear-gradient(135deg, #6a11cb, #2575fc)",
    overflow: "hidden",
  };

  const containerStyle = {
    display: "flex",
    width: "70%",
    height: "80%",
    borderRadius: "10px",
    overflow: "hidden",
  };

  const leftSideStyle = {
    width: "50%",
    background: "url('https://via.placeholder.com/500x800') center/cover no-repeat",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    color: "#fff",
    padding: "2rem",
    fontSize: "1.5rem",
    fontWeight: "bold",
    textAlign: "center",
  };

  const rightSideStyle = {
    width: "50%",
    background: "#fff",
    padding: "2rem",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    transform: `translateX(${isLogin ? "0" : "100%"})`,
    transition: "transform 0.5s ease",
  };

  const formStyle = {
    width: "80%",
    margin: "1rem 0",
  };

  const inputStyle = {
    width: "100%",
    padding: "0.8rem",
    margin: "1rem 0",
    borderRadius: "10px",
    border: "1px solid #ccc",
    fontSize: "1rem",
    outline: "none",
  };

  const buttonStyle = {
    width: "100%",
    padding: "1rem",
    background: "#6a11cb",
    color: "#fff",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    fontSize: "1.1rem",
    transition: "background 0.3s ease",
  };

  const toggleButtonStyle = {
    marginTop: "1rem",
    fontSize: "0.9rem",
    color: "#6a11cb",
    cursor: "pointer",
    textDecoration: "underline",
  };

  return (
    <div style={pageStyle}>
      <div style={containerStyle}>
        {/* Left Side Image Section */}
        <div style={leftSideStyle}>
          <div>
            <h2>Welcome to Our Website</h2>
            <p>Login or Sign Up to get started with amazing features!</p>
          </div>
        </div>

        {/* Right Side Form Section */}
        <div style={rightSideStyle}>
          {/* Login Form */}
          {isLogin ? (
            <div style={formStyle}>
              <h2>Login</h2>
              <input type="email" placeholder="Email" style={inputStyle} />
              <input type="password" placeholder="Password" style={inputStyle} />
              <button style={buttonStyle}>Login</button>
              <div style={toggleButtonStyle} onClick={toggleForm}>
                Don't have an account? Sign Up
              </div>
            </div>
          ) : (
            // Sign Up Form
            <div style={formStyle}>
              <h2>Sign Up</h2>
              <input type="text" placeholder="Full Name" style={inputStyle} />
              <input type="email" placeholder="Email" style={inputStyle} />
              <input type="password" placeholder="Password" style={inputStyle} />
              <button style={buttonStyle}>Sign Up</button>
              <div style={toggleButtonStyle} onClick={toggleForm}>
                Already have an account? Login
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
