import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const ShowProduct = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const product = {
    id,
    name: `Product ${id}`,
    price: (Math.random() * 100 + 10).toFixed(2),
    description: `This is the detailed description of Product ${id}. It has all the amazing features you need.`,
    images: [
      `https://via.placeholder.com/400?text=Product+${id}+Image+1`,
      `https://via.placeholder.com/400?text=Product+${id}+Image+2`,
      `https://via.placeholder.com/400?text=Product+${id}+Image+3`,
    ],
  };

  return (
    <div style={styles.container}>
      <button onClick={() => navigate(-1)} style={styles.backButton}>Back to Products</button>
      <div style={styles.productContainer}>
        <div style={styles.imageContainer}>
          {product.images.map((img, index) => (
            <img key={index} src={img} alt={`Product ${id} ${index + 1}`} style={styles.image} />
          ))}
        </div>
        <div style={styles.detailsContainer}>
          <h2 style={styles.productName}>{product.name}</h2>
          <p style={styles.price}>${product.price}</p>
          <p style={styles.description}>{product.description}</p>
          <button style={styles.buyButton}>Buy Now</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px 40px',
    backgroundColor: '#f4f8fb',
    textAlign: 'center',
  },
  backButton: {
    marginBottom: '20px',
    padding: '10px 15px',
    backgroundColor: '#4a90e2',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  productContainer: {
    display: 'flex',
    gap: '20px',
    alignItems: 'flex-start',
  },
  imageContainer: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  image: {
    width: '100%',
    height: '300px',
    objectFit: 'cover',
    borderRadius: '8px',
  },
  detailsContainer: {
    flex: 1,
    padding: '20px',
    backgroundColor: 'white',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
  },
  productName: {
    fontSize: '24px',
    color: '#4a90e2',
  },
  price: {
    fontSize: '20px',
    color: '#50c9c3',
    marginBottom: '10px',
  },
  description: {
    fontSize: '16px',
    color: '#555',
    marginBottom: '20px',
  },
  buyButton: {
    padding: '10px 20px',
    backgroundColor: '#4a90e2',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default ShowProduct;
