import React, { useState } from "react";

const LoginSignup = () => {
  const [isLogin, setIsLogin] = useState(true);

  const containerStyle = {
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    background: "linear-gradient(135deg, #f6d365, #fda085)",
    overflow: "hidden",
    fontFamily: "'Poppins', sans-serif",
  };

  const formWrapperStyle = {
    width: "400px",
    background: "white",
    borderRadius: "20px",
    boxShadow: "0 8px 15px rgba(0, 0, 0, 0.1)",
    padding: "2rem",
    overflow: "hidden",
    position: "relative",
    transform: "scale(1.1)",
    animation: "bounce 1.5s infinite ease-in-out",
  };

  const formStyle = {
    transform: isLogin ? "translateX(0)" : "translateX(-400px)",
    transition: "transform 0.8s cubic-bezier(0.65, 0.05, 0.36, 1)",
    display: "flex",
    flexDirection: "column",
  };

  const inputStyle = {
    margin: "10px 0",
    padding: "15px",
    fontSize: "16px",
    border: "1px solid #ddd",
    borderRadius: "5px",
    outline: "none",
    background: "#f8f9fa",
    transition: "0.3s",
  };

  const buttonStyle = {
    padding: "10px 20px",
    background: "#ff8c00",
    color: "white",
    fontWeight: "bold",
    fontSize: "16px",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    transition: "background 0.3s",
  };

  const toggleButtonStyle = {
    marginTop: "20px",
    textAlign: "center",
    color: "#007bff",
    cursor: "pointer",
    textDecoration: "underline",
    animation: "fadeIn 1.5s ease-in-out infinite alternate",
  };

  const handleToggle = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div style={containerStyle}>
      <div style={formWrapperStyle}>
        {/* Login Form */}
        <form
          style={{
            ...formStyle,
            position: "absolute",
            left: isLogin ? "0" : "400px",
          }}
        >
          <h2 style={{ marginBottom: "20px", textAlign: "center", color: "#333" }}>
            Login
          </h2>
          <input type="email" placeholder="Email" style={inputStyle} />
          <input type="password" placeholder="Password" style={inputStyle} />
          <button type="submit" style={buttonStyle}>
            Login
          </button>
        </form>

        {/* Signup Form */}
        <form
          style={{
            ...formStyle,
            position: "absolute",
            left: isLogin ? "-400px" : "0",
          }}
        >
          <h2 style={{ marginBottom: "20px", textAlign: "center", color: "#333" }}>
            Signup
          </h2>
          <input type="email" placeholder="Email" style={inputStyle} />
          <input type="password" placeholder="Password" style={inputStyle} />
          <input type="password" placeholder="Confirm Password" style={inputStyle} />
          <button type="submit" style={buttonStyle}>
            Signup
          </button>
        </form>

        {/* Toggle Button */}
        <div style={toggleButtonStyle} onClick={handleToggle}>
          {isLogin
            ? "Don't have an account? Sign up here!"
            : "Already have an account? Log in here!"}
        </div>
      </div>

      {/* Keyframe Animations */}
      <style>
        {`
          @keyframes bounce {
            0%, 100% {
              transform: scale(1.1);
            }
            50% {
              transform: scale(1.2);
            }
          }

          @keyframes fadeIn {
            from {
              opacity: 0.5;
            }
            to {
              opacity: 1;
            }
          }
        `}
      </style>
    </div>
  );
};

export default LoginSignup;
