import React from "react";
import { Link } from "react-router-dom";

const ProductPage = () => {
  const products = Array.from({ length: 24 }, (_, index) => ({
    id: index + 1,
    name: `Product ${index + 1}`,
    price: `${(index + 1) * 100} INR`,
    image: index % 2 === 0 ? `https://via.placeholder.com/150?text=Product+${index + 1}` : null,
  }));

  const pageStyle = {
    padding: "2rem",
    background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)",
    fontFamily: "Arial, sans-serif",
    color: "#333",
  };

  const gridStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "1.5rem",
  };

  const cardStyle = {
    background: "#ffffff",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    overflow: "hidden",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
  };

  const imageStyle = {
    width: "100%",
    height: "200px",
    objectFit: "cover",
    backgroundColor: "#e0e0e0",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.5rem",
    color: "#999",
  };

  const detailsStyle = {
    padding: "1rem",
    textAlign: "center",
  };

  const nameStyle = {
    fontSize: "1.2rem",
    fontWeight: "bold",
    marginBottom: "0.5rem",
  };

  const priceStyle = {
    color: "#8e44ad",
    fontSize: "1.1rem",
    fontWeight: "bold",
  };

  return (
    <div style={pageStyle}>
      <h1 style={{ textAlign: "center", marginBottom: "2rem", color: "#2c3e50" }}>
        Our Products
      </h1>
      <div style={gridStyle}>
        {products.map((product) => (
          <div key={product.id} style={cardStyle}>
            <div style={imageStyle}>
              {product.image ? (
                <img src={product.image} alt={product.name} style={{ width: "100%", height: "100%" }} />
              ) : (
                <span>No Image</span>
              )}
            </div>
            <div style={detailsStyle}>
              <div style={nameStyle}>{product.name}</div>
              <div style={priceStyle}>{product.price}</div>
              <Link to={`/product/${product.id}`}>
                <button style={{ padding: "0.5rem 1rem", background: "#3498db", color: "white", borderRadius: "20px", cursor: "pointer" }}>
                  View Details
                </button>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
