import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import products from "./products";

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const product = products.find((p) => p.id === parseInt(id));

  // Track current image for carousel
  const [currentImage, setCurrentImage] = useState(0);

  if (!product) {
    return (
      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <h1>Product Not Found</h1>
        <button
          onClick={() => navigate("/")}
          style={{
            padding: "0.5rem 1rem",
            background: "#3498db",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
          }}
        >
          Back to Products
        </button>
      </div>
    );
  }

  const nextImage = () => {
    setCurrentImage((prev) => (prev + 1) % product.images.length);
  };

  const prevImage = () => {
    setCurrentImage((prev) => (prev - 1 + product.images.length) % product.images.length);
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>{product.name}</h1>
      <div style={{ position: "relative", marginBottom: "1rem" }}>
        <img
          src={product.images[currentImage]}
          alt={`Product ${product.id} - ${currentImage + 1}`}
          style={{
            width: "100%",
            maxWidth: "500px",
            borderRadius: "10px",
            boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
          }}
        />
        <button
          onClick={prevImage}
          style={{
            position: "absolute",
            top: "50%",
            left: "0",
            transform: "translateY(-50%)",
            background: "rgba(0, 0, 0, 0.5)",
            color: "#fff",
            border: "none",
            borderRadius: "50%",
            padding: "0.5rem",
            cursor: "pointer",
          }}
        >
          &lt;
        </button>
        <button
          onClick={nextImage}
          style={{
            position: "absolute",
            top: "50%",
            right: "0",
            transform: "translateY(-50%)",
            background: "rgba(0, 0, 0, 0.5)",
            color: "#fff",
            border: "none",
            borderRadius: "50%",
            padding: "0.5rem",
            cursor: "pointer",
          }}
        >
          &gt;
        </button>
      </div>
      <p style={{ color: "#8e44ad", fontWeight: "bold", fontSize: "1.5rem" }}>{product.price}</p>
      <p style={{ maxWidth: "600px", margin: "0 auto", fontSize: "1rem", color: "#2c3e50" }}>
        {product.description}
      </p>
      <p style={{ marginTop: "1rem", fontSize: "0.9rem", color: "#7f8c8d" }}>{product.terms}</p>
      <button
        onClick={() => navigate("/")}
        style={{
          marginTop: "1rem",
          padding: "0.5rem 1rem",
          background: "#3498db",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Back to Products
      </button>
    </div>
  );
};

export default ProductDetail;
