import React from "react";
import { useParams, useLocation } from "react-router-dom";

const ProductDetail = () => {
  const { productId } = useParams();
  const location = useLocation();
  const { price } = location.state || {};  // Get price from location state

  const products = Array.from({ length: 24 }, (_, index) => ({
    id: index + 1,
    name: `Product ${index + 1}`,
    description: `This is a detailed description of Product ${index + 1}.`,
    terms: `These are the terms and conditions for Product ${index + 1}.`,
    image: index % 2 === 0 ? `https://via.placeholder.com/150?text=Product+${index + 1}` : null,
  }));

  const product = products.find((prod) => prod.id === parseInt(productId));

  const pageStyle = {
    padding: "2rem",
    background: "#fff",
    fontFamily: "Arial, sans-serif",
    color: "#333",
  };

  const detailStyle = {
    display: "flex",
    gap: "2rem",
    justifyContent: "center",
    alignItems: "flex-start",
  };

  const imageStyle = {
    width: "300px",
    height: "300px",
    objectFit: "cover",
    borderRadius: "10px",
    backgroundColor: "#e0e0e0",
  };

  const contentStyle = {
    maxWidth: "600px",
  };

  const nameStyle = {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "1rem",
  };

  const descriptionStyle = {
    marginBottom: "1rem",
  };

  const termsStyle = {
    marginTop: "2rem",
    fontStyle: "italic",
  };

  return (
    <div style={pageStyle}>
      {product ? (
        <div style={detailStyle}>
          <div>
            <img src={product.image || "https://via.placeholder.com/300?text=No+Image"} alt={product.name} style={imageStyle} />
          </div>
          <div style={contentStyle}>
            <div style={nameStyle}>{product.name}</div>
            <div style={descriptionStyle}>{product.description}</div>
            <div style={termsStyle}>{product.terms}</div>
            <div style={{ fontSize: "1.5rem", fontWeight: "bold", marginTop: "1rem" }}>
              Price: {price || "Price not available"}  {/* Display the price */}
            </div>
          </div>
        </div>
      ) : (
        <div>Product not found.</div>
      )}
    </div>
  );
};

export default ProductDetail;
