import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const OrderForm = () => {
  const { id } = useParams(); // Extract product ID from URL
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [orderData, setOrderData] = useState({
    quantity: 1,
    address: "",
    contact: "",
    paymentMethod: "Online",
  });

  // Product list stored locally
  const products = [
    { id: "1", name: "Product 1", price: 500, image: "https://via.placeholder.com/150?text=Product+1" },
    { id: "2", name: "Product 2", price: 1000, image: "https://via.placeholder.com/150?text=Product+2" },
    { id: "3", name: "Product 3", price: 1500, image: "https://via.placeholder.com/150?text=Product+3" },
    // Add up to 24 products with unique IDs, prices, and optional images
  ];

  // Find the product based on the ID from the URL
  const product = products.find((p) => p.id === id);

  if (!product) {
    alert("Product not found!");
    navigate("/");
    return null;
  }

  const handleNext = () => setStep((prev) => prev + 1);
  const handlePrevious = () => setStep((prev) => prev - 1);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderData((prev) => ({ ...prev, [name]: value }));
  };

  const handleOrderConfirmation = () => {
    alert("Order placed successfully!");
    navigate("/");
  };

  const containerStyle = {
    padding: "2rem",
    fontFamily: "Arial, sans-serif",
    background: "linear-gradient(135deg, #ffffff, #f7f9fc)",
    color: "#333",
    maxWidth: "600px",
    margin: "2rem auto",
    borderRadius: "15px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  };

  const buttonStyle = {
    padding: "0.8rem 2rem",
    background: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    margin: "1rem 0.5rem",
    fontSize: "1rem",
    transition: "background 0.3s ease",
  };

  const inputStyle = {
    width: "100%",
    padding: "0.8rem",
    marginBottom: "1rem",
    borderRadius: "5px",
    border: "1px solid #ccc",
  };

  return (
    <div style={containerStyle}>
      <h2 style={{ textAlign: "center", marginBottom: "1.5rem" }}>Order {product.name}</h2>
      {step === 1 && (
        <>
          <h3>Step 1: Select Quantity</h3>
          <input
            type="number"
            name="quantity"
            value={orderData.quantity}
            min="1"
            onChange={handleInputChange}
            style={inputStyle}
          />
          <button style={buttonStyle} onClick={handleNext}>
            Next
          </button>
        </>
      )}
      {step === 2 && (
        <>
          <h3>Step 2: Enter Address and Contact</h3>
          <textarea
            name="address"
            placeholder="Enter your address"
            value={orderData.address}
            onChange={handleInputChange}
            rows="4"
            style={inputStyle}
          ></textarea>
          <input
            type="text"
            name="contact"
            placeholder="Enter your contact details"
            value={orderData.contact}
            onChange={handleInputChange}
            style={inputStyle}
          />
          <button style={buttonStyle} onClick={handlePrevious}>
            Previous
          </button>
          <button style={buttonStyle} onClick={handleNext}>
            Next
          </button>
        </>
      )}
      {step === 3 && (
        <>
          <h3>Step 3: Choose Payment Method</h3>
          <select
            name="paymentMethod"
            value={orderData.paymentMethod}
            onChange={handleInputChange}
            style={inputStyle}
          >
            <option value="Online">Online Payment</option>
            <option value="COD">Cash on Delivery</option>
          </select>
          <button style={buttonStyle} onClick={handlePrevious}>
            Previous
          </button>
          <button style={buttonStyle} onClick={handleNext}>
            Next
          </button>
        </>
      )}
      {step === 4 && (
        <>
          <h3>Step 4: Order Summary</h3>
          <div style={{ textAlign: "center" }}>
            <img
              src={product.image || "https://via.placeholder.com/150?text=No+Image"}
              alt={product.name}
              style={{ width: "100%", maxHeight: "200px", marginBottom: "1rem" }}
            />
            <p><strong>Product Name:</strong> {product.name}</p>
            <p><strong>Product ID:</strong> {product.id}</p>
            <p><strong>Price:</strong> {product.price} INR</p>
            <p><strong>Quantity:</strong> {orderData.quantity}</p>
            <p><strong>Total:</strong> {product.price * orderData.quantity} INR</p>
            <p><strong>Address:</strong> {orderData.address}</p>
            <p><strong>Contact:</strong> {orderData.contact}</p>
            <p><strong>Payment Method:</strong> {orderData.paymentMethod}</p>
          </div>
          <button style={buttonStyle} onClick={handlePrevious}>
            Previous
          </button>
          <button style={buttonStyle} onClick={handleOrderConfirmation}>
            Confirm Order
          </button>
        </>
      )}
    </div>
  );
};

export default OrderForm;
