import React, { useState } from "react";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => setIsLogin(!isLogin);

  const pageStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontFamily: "Arial, sans-serif",
    background: "linear-gradient(135deg, #6a11cb, #2575fc)",
  };

  const containerStyle = {
    width: "350px",
    background: "#ffffff",
    padding: "2rem",
    borderRadius: "15px",
    boxShadow: "0 8px 15px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
    overflow: "hidden",
  };

  const sliderStyle = {
    display: "flex",
    transform: `translateX(${isLogin ? "0" : "-100%"})`,
    transition: "transform 0.5s ease",
    width: "200%",
  };

  const formStyle = {
    width: "50%",
    padding: "1rem",
    boxSizing: "border-box",
  };

  const buttonStyle = {
    marginTop: "1rem",
    padding: "0.8rem 1.5rem",
    background: "#6a11cb",
    color: "white",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    fontSize: "1rem",
    transition: "background 0.3s ease",
  };

  const toggleButtonStyle = {
    marginTop: "1rem",
    fontSize: "0.9rem",
    color: "#6a11cb",
    cursor: "pointer",
    textDecoration: "underline",
  };

  return (
    <div style={pageStyle}>
      <div style={containerStyle}>
        <div style={{ overflow: "hidden", width: "100%" }}>
          <div style={sliderStyle}>
            {/* Login Form */}
            <div style={formStyle}>
              <h2>Login</h2>
              <input
                type="email"
                placeholder="Email"
                style={{ width: "100%", padding: "0.8rem", margin: "0.5rem 0", borderRadius: "5px", border: "1px solid #ccc" }}
              />
              <input
                type="password"
                placeholder="Password"
                style={{ width: "100%", padding: "0.8rem", margin: "0.5rem 0", borderRadius: "5px", border: "1px solid #ccc" }}
              />
              <button style={buttonStyle}>Login</button>
              <div style={toggleButtonStyle} onClick={toggleForm}>
                Don't have an account? Sign Up
              </div>
            </div>

            {/* Signup Form */}
            <div style={formStyle}>
              <h2>Sign Up</h2>
              <input
                type="text"
                placeholder="Name"
                style={{ width: "100%", padding: "0.8rem", margin: "0.5rem 0", borderRadius: "5px", border: "1px solid #ccc" }}
              />
              <input
                type="email"
                placeholder="Email"
                style={{ width: "100%", padding: "0.8rem", margin: "0.5rem 0", borderRadius: "5px", border: "1px solid #ccc" }}
              />
              <input
                type="password"
                placeholder="Password"
                style={{ width: "100%", padding: "0.8rem", margin: "0.5rem 0", borderRadius: "5px", border: "1px solid #ccc" }}
              />
              <button style={buttonStyle}>Sign Up</button>
              <div style={toggleButtonStyle} onClick={toggleForm}>
                Already have an account? Login
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
